function _isHtml5() {
    var ie = __ieversion;
    if (ie)
        return ie >= 9;
    else
        return document.doctype != null && document.doctype.name != null && document.doctype.name.toLowerCase() == "html"
};
function getAndroidVersion() {
    var s = window.navigator.userAgent;
    var p = s.indexOf("Android");
    if (p < 0)
        return false;
    s = s.substr(p + 8);
    p = s.indexOf(';');
    s = s.substr(0, p);
    p = s.indexOf('.');
    if (p > 0)
        s = s.substr(0, p);
    return isNaN(s) ? true : parseFloat(s);
};
function getiOSVersion() {
    var s = window.navigator.userAgent;
    var p = s.indexOf("iPad");
    if (p < 0)
        p = s.indexOf("iPhone");
    if (p < 0)
        return false;
    var p = s.indexOf('OS', p + 4);
    if (p < 0)
        return true;
    s = s.substr(p + 3);
    p = s.indexOf('_');
    s = s.substr(0, p);
    p = s.indexOf('.');
    if (p > 0)
        s = s.substr(0, p);
    return isNaN(s) ? true : parseInt(s);
};

