    splitCsvRow: function (s) {
        if (s == null || s == "\r" || s == "")
            return;

        if (s.substr(s.length - 1) == "\r")
            s = s.substr(0, s.length - 1);

        var ret = [];

        var inquote = false;
        var v = "";
        for (var i = 0; i < s.length; ++i) {
            var c = s.substr(i, 1);
            if (c == '\"') {
                if (!inquote) {
                    if (v == "")
                        inquote = true;
                    else
                        v += c;
                }
                else {
                    if (i < s.length - 1) {
                        var c1 = s.substr(i + 1, 1);
                        if (c1 == '\"') {
                            v += c;
                            ++i;
                        }
                        else if (c1 == ',') {
                            ret.push(v);
                            v = "";
                            inquote = false;
                            ++i;
                        }
                        else {
                            v += c;
                        }
                    }
                    else {
                        inquote = false;
                    }
                }
            }
            else if (c == ',') {
                if (inquote) {
                    v += c;
                }
                else {
                    ret.push(v);
                    v = "";
                }
            }
        }

        ret.push(v);

        return ret;
    },

    escCsvValue: function (v) {
        if (v == null)
            return "";
        if (typeof (v) != "string")
            v = v + "";
        if (v.indexOf(',') >= 0 || v.indexOf('\"') >= 0 || v.indexOf('\r') >= 0 || v.indexOf('\n') >= 0)
            v = '\"' + v.replace(/[\"]/g, "\"\"").replace(/\r\n/g, "") + '\"';
        return v;
    },

