    /**
    * Parse an xml string
    * @function {static} parseXml
    * @param {string} xml - input xml string
    * @returns a XML document object
    */
    parseXml: function (xml) {
        var ret = this.parseXml2(xml);
        if (ret != null && ret.succeeded)
            return ret.doc;
        return null;
    },

    parseXml2: function (xml) {
        if (xml == null)
            return null;

        xml = this.trim(xml);
        if (xml.length < 10 || xml.substr(0, 1) != "<" || xml.substr(xml.length - 1, 1) != ">")
            return null;

        var succeeded = false;
        var error = null;
        var doc = null;
        try {
            if (window.DOMParser) {
                doc = new DOMParser().parseFromString(xml, "text/xml");
            }
            else // Internet Explorer
            {
                doc = new ActiveXObject("Microsoft.XMLDOM");
                doc.async = "false";
                doc.loadXML(xml);
            }
            succeeded = true;
        }
        catch (e) {
            error = e.message;
        }

        return { succeeded: succeeded, error: error, doc: doc };
    },

    xquery: function (e, path, returnone) {
        if (e == null || path == null || path == "")
            return null;

        var ret = null;
        var list = path.split('/');
        for (var i = 0; i < list.length; ++i) {
            var s = list[i];
            if (s == "")
                continue;

            var tag = s.replace(/[\[]@[a-z]+[0-9|a-z]{0,9}='[^\']+'[\]]$/, "");
            s = s.substr(tag.length + 2, s.length - (tag.length + 3));

            var key = null;
            var val = null;
            var p = s.indexOf('=');
            if (p > 0) {
                key = s.substr(0, p);
                p += 2;
                val = s.substr(p, s.length - p - 1);
            }

            var e2 = null;
            for (var k = 0; k < e.childNodes.length; ++k) {
                var c = e.childNodes[k];
                if (c.tagName == tag) {
                    if (key == null || c.getAttribute(key) == val)
                        e2 = c;
                }

                if (e2 != null) {
                    if (i == list.length - 1) {
                        if (returnone)
                            return e2;

                        if (ret == null)
                            ret = [e2];
                        else
                            ret.push(e2);
                    }
                    else {
                        e2 = c;
                        break;
                    }
                }
            }

            if (ret != null)
                return ret;
            if (e2 == null)
                return null;
            else
                e = e2;
        }
        return null;
    },

