    getChiralAtom: function (t) {
        if (t == null || t.anchors == null || t.anchors.length != 1 || t.fieldtype != "CHIRAL")
            return null;
        var a = JSDraw2.Atom.cast(t.anchors[0]);
        if (a == null)
            return null;
        return JSDraw2.Atom.isValidChiral(t.text) ? a : null;
    },

