    toggleAtom: function (p, tor) {
        for (var i = 0; i < this.atoms.length; ++i) {
            var a = this.atoms[i];
            if (a.toggle(p, tor))
                return a;

            if (a.rgroup != null) {
                var list = a.rgroup.mols;
                for (var j = 0; j < list.length; ++j) {
                    var r = list[j].toggleAtom(p, tor);
                    if (r != null)
                        return r;
                }
            }
        }
        return null;
    },

    toggle: function (p, tor) {
        for (var i = 0; i < this.atoms.length; ++i) {
            var a = this.atoms[i];
            if (a.toggle(p, tor))
                return a;

            if (a.rgroup == null)
                continue;

            if (a.rgroup.toggle(p, tor))
                return a.rgroup;

            var list = a.rgroup.mols;
            for (var j = 0; j < list.length; ++j) {
                var r = list[j].toggle(p, tor);
                if (r != null)
                    return r;
            }
        }

        for (var i = 0; i < this.bonds.length; ++i) {
            var b = this.bonds[i];
            if (b.toggle(p, tor))
                return this.bonds[i];
        }

        for (var i = this.graphics.length - 1; i >= 0; --i) {
            var g = this.graphics[i];
            if (g.toggle(p, tor))
                return this.graphics[i];
        }
        return null;
    },

