            return;

        if (this.bondlength * s < JSDraw2.speedup.minbondlength)
            s = JSDraw2.speedup.minbondlength / this.bondlength;

        this.m.scale(s, origin);
        this.bondlength *= s;
        this.tor *= s;
        this.linewidth *= s;
        this.fontsize *= s;

        if (origin == null)
            this.moveCenter();
