        if (num != s) {
            unit2 = s.substr(num.length);
            unit = unit2.toLowerCase();
            num = scil.Utils.trim(num);
        }

        if ((unit == "w/w" || unit == "w/v") && scil.Utils.endswith(num, "%")) {
            num = num.substr(0, num.length - 1);
            unit2 = unit = "% " + unit;
        }

        if (unit == "%w/w")
            unit2 = unit = "% w/w";
        else if (unit == "%w/v")
            unit2 = unit = "% w/v";

        return { value: scil.Utils.trim(num), unit: unit, unit2: unit2 };
